function cartas() {
  var _return = [];
  for (let i in cards) {
    _return.push(`
    <a href="./cuento.php?cuento=${[i]}&title=${cards[i].title}" class=" w3-btn col-lg-3 w3-border w3-round text-wrap" id="cartas">
        <header class="">
            <h3 class="w3-border-bottom w3-dark-grey w3-block w3-center">${cards[i].title}</h3>
        </header>
        <img src="${cards[i].img}" alt="${cards[i].title}" class="h-75 w-75 img-thumbnail">
        <p class="w3-left-align "> <strong> ${cards[i].desc}</strong> </p>
        <footer class="text-muted text-sm-end ">
            ${cards[i].date}
        </footer>
    </a>`);
  }

  return _return.join("");
};

function test(text, open, close, _endvar) {
    var _split = text.split("\n");
    var _return = [];
    for (let index = 0; index < _split.length; index++) {
    _return.push(`${open}${_split[index]}${close}`);
    }
    return (_endvar = _return.join("\n"));
};
